mapper.insert:
INSERT INTO mydb.NOTICE 
(			NOTI_NO
		  , USER_NO
		  , NOTI_TITLE
		  , NOTI_CONTENT
		  , NOTI_DATE
		  , NOTI_IMG
)
VALUES(
			(SELECT IFNULL(MAX(N.NOTI_NO),0) + 1 FROM mydb.NOTICE N)
		  , #{noti_title}
		  , #{noti_content}
		  , #{noti_date}
		  , #{noti_img}
)

mapper.update:
UPDATE NOTICE
SET 	NOTI_TITLE = #{noti_title}
	  , NOTI_CONTENT = #{noti_content}
	  , NOTI_UPD_DATE = #{noti_upd_date}
	   , NOTI_IMG = #{noti_img}

mapper.select:
SELECT FAQ_NO, FAQ_TITLE, FAQ_CONTENT, FAQ_DATE, FAQ_CATEGORY, FAQ_COUNT
FROM mydb.FAQ
WHERE FAQ_NO = #{faq_no}

mapper.insert:
INSERT INTO mydb.FAQ 
(			FAQ_NO
		  , FAQ_TITLE
		  , FAQ_CONTENT
		  , FAQ_DATE
		  , FAQ_CATEGORY
)
VALUES(		   
		   (SELECT IFNULL(MAX(F.FAQ_NO),0) + 1 FROM mydb.FAQ F)
		 , #{faq_title}
		 , #{faq_content}
		 , NOW()
		 , #{faq_category}
)

mapper.update:
UPDATE FAQ

SET FAQ_TITLE = #{faq_title}
  , FAQ_CONTENT = #{faq_content}
  , FAQ_UPD_DATE = NOW() 
  , FAQ_CATEGORY = #{faq_category}

WHERE FAQ_NO = #{faq_no}

mapper.select:
SELECT FAQ_NO, FAQ_CATEGORY, FAQ_TITLE, FAQ_DATE
FROM FAQ
WHERE FAQ_CATEGORY = #{faq_category}

mapper.insert:
INSERT INTO mydb.QNA 
(			Q_RE_NO
		  , Q_RE_TITLE
		  , Q_RE_CONTENT
		  , Q_RE_DATE
)
VALUES(
		   (SELECT IFNULL(MAX(Q.Q_NO),0) + 1 FROM QNA Q)
		 , #{q_re_title}
		 , #{q_re_content}
		 , NOW() 
)

mapper.update:
UPDATE QNA

SET 	Q_RE_TITLE = #{q_re_title}
	  , Q_RE_CONTENT = #{q_re_content}
	  , Q_RE_UPD_DATE = #{q_re_upd_date}

WHERE Q_RE_NO = #{q_re_no}

mapper.insert:
INSERT INTO mydb.COURSE 
(			C_NO
		  , USER_NO
		  , C_NAME
		  , C_CATEGORY
		  , C_TYPE
		  , C_TIME
		  , C_DETAIL
		  , ENT_PERSONNEL
		  , C_REGDATE
		  , C_INIT_WRITER
		  , C_START_DATE
		  , C_LAST_DATE
		  , C_START_TIME 
)
VALUES
(		   (SELECT IFNULL(MAX(C.C_NO),0) + 1 FROM mydb.COURSE C)
		 , '2'
		 , #{c_name}
		 , #{c_category}
		 , #{c_type}
		 , #{c_time}
		 , #{c_detail}
		 , #{ent_personnel}
		 , NOW() 
		 , '관리자'
		 , #{c_start_date}
		 , #{c_last_date}
		 , #{c_start_time}
)

mapper.update:
UPDATE COURSE

SET 	C_NAME = #{c_name}
	  , C_CATEGORY = #{c_category}
	  , C_TYPE = #{c_type}
	  , C_TIME = #{c_time}
	  , C_START_TIME = #{c_start_time}
	  , C_DETAIL = #{c_detail}
	  , ENT_PERSONNEL = #{ent_personnel}
	  , C_UPD_DATE = NOW()
	  , C_UPD_WRITER = '관리자'
	  , C_START_DATE = #{c_start_date}
	  , C_LAST_DATE = #{c_last_date}

WHERE C_NO = #{c_no}
