mapper.select:
SELECT COUNT(*) 
FROM mydb.MEMBER
WHERE USER_ID=#{user_id}

mapper.insert:
INSERT INTO mydb.MEMBER 
(		  USER_NO 
		, USER_ID 
		, USER_PW 
		, USER_NAME 
		, USER_EMAIL 
		, USER_ADDR 
		, USER_PHONE 
		, USER_ROLE
		, USER_ENABLE 
		, USER_REGDATE 
)
VALUES (
  		  (SELECT IFNULL(MAX(M.USER_NO),0) + 1 FROM MEMBER M) 
		, #{user_id}
		, #{user_pw} 
		, #{user_name} 
		, #{user_email} 
		, #{user_addr}  
		, #{user_phone} 
		, 'U'
		, 'Y' 
		, NOW() 
)

mapper.update:
UPDATE mydb.MEMBER 

SET   USER_PW = #{user_pw}
	, USER_EMAIL = #{user_email}
	, USER_ADDR = #{user_addr}
	, USER_ENABLE = #{user_enable}
	, USER_UPD_DATE = SYSDATE

WHERE USER_NO = #{user_no}

UPDATE mydb.MEMBER 
SET	USER_ENABLE = 'N'
WHERE USER_NO = #{user_no}
